import * as React from 'react';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import Checkbox from '@mui/material/Checkbox';
import CssBaseline from '@mui/material/CssBaseline';
import Divider from '@mui/material/Divider';
import FormControlLabel from '@mui/material/FormControlLabel';
import FormLabel from '@mui/material/FormLabel';
import FormControl from '@mui/material/FormControl';
import Link from '@mui/material/Link';
import TextField from '@mui/material/TextField';
import Typography from '@mui/material/Typography';
import Stack from '@mui/material/Stack';
import MuiCard from '@mui/material/Card';
import { styled } from '@mui/material/styles';
import AppTheme from '../../components/shared-theme/AppTheme';
import Grid from '@mui/material/Grid';

const Card = styled(MuiCard)(({ theme }) => ({
  display: 'flex',
  flexDirection: 'column',
  alignSelf: 'center',
  width: '100%',
  padding: theme.spacing(4),
  gap: theme.spacing(2),
  margin: 'auto',
  boxShadow:
    'hsla(220, 30%, 5%, 0.05) 0px 5px 15px 0px, hsla(220, 25%, 10%, 0.05) 0px 15px 35px -5px',
  [theme.breakpoints.up('sm')]: {
    width: '450px',
  },
  ...theme.applyStyles('dark', {
    boxShadow:
      'hsla(220, 30%, 5%, 0.5) 0px 5px 15px 0px, hsla(220, 25%, 10%, 0.08) 0px 15px 35px -5px',
  }),
}));

const SignUpContainer = styled(Stack)(({ theme }) => ({
  height: "100vh",
  minHeight: "100%",
  padding: theme.spacing(2),
  position: "relative",
  display: "flex",
  alignItems: "center",
  justifyContent: "center",
  [theme.breakpoints.up("sm")]: {
    padding: theme.spacing(4),
  },

  "&::before": {
    content: '""',
    position: "absolute",
    top: 0,
    left: 0,
    width: "100%",
    height: "100%",
    backgroundImage: "url('/background.jpg')",
    backgroundSize: "cover",
    backgroundPosition: "center",
    backgroundRepeat: "no-repeat",
    filter: "blur(10px)",
    zIndex: -1,
  },
}));

export default function SignUp(props) {
  const [formData, setFormData] = React.useState({
    firstName: '',
    lastName: '',
    email: '',
    phone: '',
    cin: '',
    address: '',
    specialty: '',
    dateOfBirth: '',
    password: '',
    allowExtraEmails: false
  });

  const [errors, setErrors] = React.useState({
    firstName: '',
    lastName: '',
    email: '',
    password: '',
    phone: '',
    cin: '',
    address: '',
    specialty: '',
    dateOf: ''
  });

  const handleChange = (event) => {
    const { name, value, type, checked } = event.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));

    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const validateInputs = () => {
    const newErrors = {};

    if (!formData.email || !/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'Veuillez entrer une adresse email valide.';
    }

    if (!formData.password || formData.password.length < 6) {
      newErrors.password = 'Le mot de passe doit contenir au moins 6 caractères.';
    }

    if (!formData.firstName) {
      newErrors.firstName = 'Le prénom est requis.';
    }

    if (!formData.lastName) {
      newErrors.lastName = 'Le nom est requis.';
    }

    if (!formData.phone || !/^\+?\d{8,}$/.test(formData.phone)) {
      newErrors.phone = 'Numéro de téléphone invalide.';
    }

    if (!formData.cin || !/^\d{8}$/.test(formData.cin)) {
      newErrors.cin = 'CIN invalide (8 chiffres requis).';
    }

    if (!formData.address) {
      newErrors.address = 'L\'adresse est requise.';
    }

    if (!formData.specialty) {
      newErrors.specialty = 'La spécialité est requise.';
    }

    if (!formData.dob) {
      newErrors.dob = 'La date de naissance est requise.';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (!validateInputs()) {
      return;
    }

    const payload = {
      ...formData,
      role: 'doctor'
    };

    try {
      const response = await fetch(`${process.env.REACT_APP_API_URL}/auth/register`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(payload),
      });

      if (!response.ok) {
        throw new Error('Une erreur est survenue lors de l\'inscription.');
      }
      const result = await response.json();
      console.log("Réponse du serveur:", result);
    } 
    catch (error) {
      console.error(error);
    }
  };

  return (
    <AppTheme {...props}>
      <CssBaseline enableColorScheme />
      <SignUpContainer direction="column">
        <Card variant="outlined">
          <Typography
            component="h1"
            variant="h4"
            sx={{ width: '100%', fontSize: 'clamp(2rem, 10vw, 2.15rem)' }}
          >
            S'inscrire
          </Typography>
          <Box component="form" sx={{ display: 'flex', flexDirection: 'column', gap: 2 }} onSubmit={handleSubmit}>
            <Grid container spacing={2}>
              <Grid item xs={12} sm={6}>
                <FormControl fullWidth>
                  <FormLabel>Prénom</FormLabel>
                  <TextField
                    required
                    fullWidth
                    name="firstName"
                    value={formData.firstName}
                    onChange={handleChange}
                    error={!!errors.firstName}
                    helperText={errors.firstName}
                    placeholder="John"
                  />
                </FormControl>
              </Grid>

              <Grid item xs={12} sm={6}>
                <FormControl fullWidth>
                  <FormLabel>Nom</FormLabel>
                  <TextField
                    required
                    fullWidth
                    name="lastName"
                    value={formData.lastName}
                    onChange={handleChange}
                    error={!!errors.lastName}
                    helperText={errors.lastName}
                    placeholder="Doe"
                  />
                </FormControl>
              </Grid>

              <Grid item xs={12} sm={6}>
                <FormControl fullWidth>
                  <FormLabel>Email</FormLabel>
                  <TextField
                    required
                    fullWidth
                    name="email"
                    type="email"
                    value={formData.email}
                    onChange={handleChange}
                    error={!!errors.email}
                    helperText={errors.email}
                    placeholder="exemple@email.com"
                  />
                </FormControl>
              </Grid>

              <Grid item xs={12} sm={6}>
                <FormControl fullWidth>
                  <FormLabel>Téléphone</FormLabel>
                  <TextField
                    required
                    fullWidth
                    name="phone"
                    value={formData.phone}
                    onChange={handleChange}
                    error={!!errors.phone}
                    helperText={errors.phone}
                    placeholder="+216 99 999 999"
                  />
                </FormControl>
              </Grid>

              <Grid item xs={12} sm={6}>
                <FormControl fullWidth>
                  <FormLabel>CIN</FormLabel>
                  <TextField
                    required
                    fullWidth
                    name="cin"
                    value={formData.cin}
                    onChange={handleChange}
                    error={!!errors.cin}
                    helperText={errors.cin}
                    placeholder="12345678"
                  />
                </FormControl>
              </Grid>

              <Grid item xs={12} sm={6}>
                <FormControl fullWidth>
                  <FormLabel>Adresse</FormLabel>
                  <TextField
                    required
                    fullWidth
                    name="address"
                    value={formData.address}
                    onChange={handleChange}
                    error={!!errors.address}
                    helperText={errors.address}
                    placeholder="Rue, Ville, Pays"
                  />
                </FormControl>
              </Grid>

              <Grid item xs={12} sm={6}>
                <FormControl fullWidth>
                  <FormLabel>Spécialité</FormLabel>
                  <TextField
                    required
                    fullWidth
                    name="specialty"
                    value={formData.specialty}
                    onChange={handleChange}
                    error={!!errors.specialty}
                    helperText={errors.specialty}
                    placeholder="Dentiste"
                  />
                </FormControl>
              </Grid>

              <Grid item xs={12} sm={6}>
                <FormControl fullWidth>
                  <FormLabel>Date de naissance</FormLabel>
                  <TextField
                    required
                    fullWidth
                    name="dob"
                    type="date"
                    value={formData.dob}
                    onChange={handleChange}
                    error={!!errors.dob}
                    helperText={errors.dob}
                  />
                </FormControl>
              </Grid>

              <Grid item xs={12} sm={6}>
                <FormControl fullWidth>
                  <FormLabel>Mot de passe</FormLabel>
                  <TextField
                    required
                    fullWidth
                    name="password"
                    type="password"
                    value={formData.password}
                    onChange={handleChange}
                    error={!!errors.password}
                    helperText={errors.password}
                    placeholder="••••••"
                  />
                </FormControl>
              </Grid>
            </Grid>

            <FormControlLabel
              control={
                <Checkbox
                  name="allowExtraEmails"
                  checked={formData.allowExtraEmails}
                  onChange={handleChange}
                />
              }
              label="Recevoir des mises à jour par email"
            />

            <Button type="submit" fullWidth variant="contained">
              S'inscrire
            </Button>
          </Box>

          <Divider><Typography>ou</Typography></Divider>

          <Typography sx={{ textAlign: 'center' }}>
            Déjà un compte ? <Link href="/signin" variant="body2">Se connecter</Link>
          </Typography>
        </Card>
      </SignUpContainer>
    </AppTheme>
  );
}
