import * as React from 'react';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import Checkbox from '@mui/material/Checkbox';
import CssBaseline from '@mui/material/CssBaseline';
import Divider from '@mui/material/Divider';
import FormControlLabel from '@mui/material/FormControlLabel';
import FormLabel from '@mui/material/FormLabel';
import FormControl from '@mui/material/FormControl';
import Link from '@mui/material/Link';
import TextField from '@mui/material/TextField';
import Typography from '@mui/material/Typography';
import Stack from '@mui/material/Stack';
import MuiCard from '@mui/material/Card';
import { styled } from '@mui/material/styles';
import AppTheme from '../../components/shared-theme/AppTheme';
import Grid from '@mui/material/Grid';
import Tooltip from '@mui/material/Tooltip';

const Card = styled(MuiCard)(({ theme }) => ({
  display: 'flex',
  flexDirection: 'column',
  alignSelf: 'center',
  width: '100%',
  padding: theme.spacing(4),
  gap: theme.spacing(2),
  margin: 'auto',
  boxShadow:
    'hsla(220, 30%, 5%, 0.05) 0px 5px 15px 0px, hsla(220, 25%, 10%, 0.05) 0px 15px 35px -5px',
  [theme.breakpoints.up('sm')]: {
    width: '450px',
  },
  ...theme.applyStyles('dark', {
    boxShadow:
      'hsla(220, 30%, 5%, 0.5) 0px 5px 15px 0px, hsla(220, 25%, 10%, 0.08) 0px 15px 35px -5px',
  }),
}));

const SignUpContainer = styled(Stack)(({ theme }) => ({
  height: "105vh",
  minHeight: "100%",
  padding: theme.spacing(2),
  position: "relative",
  display: "flex",
  alignItems: "center",
  justifyContent: "center",
  [theme.breakpoints.up("sm")]: {
    padding: theme.spacing(4),
  },
  "&::before": {
    content: '""',
    position: "absolute",
    top: 0,
    left: 0,
    width: "100%",
    height: "100%",
    backgroundImage: "url('/background.jpg')",
    backgroundSize: "cover",
    backgroundPosition: "center",
    backgroundRepeat: "no-repeat",
    filter: "blur(10px)",
    zIndex: -1,
  },
}));

export default function SignUp(props) {
  const [formData, setFormData] = React.useState({
    firstName: '',
    lastName: '',
    email: '',
    phone: '',
    cin: '',
    address: '',
    speciality: '',
    dateOfBirth: '',
    password: ''
  });

  const [errors, setErrors] = React.useState({});

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData(prev => ({ ...prev, [name]: value }));
    if (errors[name]) {
      setErrors(prev => ({ ...prev, [name]: '' }));
    }
  };

  const validateInputs = () => {
    const newErrors = {};
    if (!formData.email || !/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'Veuillez entrer une adresse email valide.';
    }
    if (!formData.password || formData.password.length < 6) {
      newErrors.password = 'Le mot de passe doit contenir au moins 6 caractères.';
    }
    if (!formData.firstName) newErrors.firstName = 'Le prénom est requis.';
    if (!formData.lastName) newErrors.lastName = 'Le nom est requis.';
    if (!formData.phone || !/^\+?\d{8,}$/.test(formData.phone)) {
      newErrors.phone = 'Numéro de téléphone invalide.';
    }
    if (!formData.cin || !/^\d{8}$/.test(formData.cin)) {
      newErrors.cin = 'CIN invalide (8 chiffres requis).';
    }
    if (!formData.address) newErrors.address = 'L\'adresse est requise.';
    if (!formData.speciality) newErrors.speciality = 'La spécialité est requise.';
    if (!formData.dateOfBirth) newErrors.dateOfBirth = 'La date de naissance est requise.';

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (!validateInputs()) return;
  };

  return (
    <AppTheme {...props}>
      <CssBaseline enableColorScheme />
      <SignUpContainer direction="column">
        {errors.userExists && <Typography color="error">{errors.userExists}</Typography>}
        <Card variant="outlined">
          <Typography component="h1" variant="h4">S'inscrire</Typography>
          <Box component="form" onSubmit={handleSubmit}>
            <Grid container spacing={2}>
              {Object.keys(formData).map((key) => (
                <Grid item xs={12} sm={6} key={key}>
                  <FormControl fullWidth>
                    <FormLabel>{key}</FormLabel>
                    <Tooltip title={errors[key] || ""} open={!!errors[key]} arrow>
                      <TextField 
                        required 
                        name={key} 
                        value={formData[key]} 
                        onChange={handleChange} 
                        error={!!errors[key]} 
                        sx={{
                          '& .MuiOutlinedInput-root': {
                            '& fieldset': {
                              borderColor: errors[key] ? 'red' : '',
                            }
                          }
                        }}
                      />
                    </Tooltip>
                  </FormControl>
                </Grid>
              ))}
            </Grid>
            <Button type="submit" fullWidth variant="contained">S'inscrire</Button>
          </Box>
        </Card>
      </SignUpContainer>
    </AppTheme>
  );
}
