import * as React from 'react';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import Checkbox from '@mui/material/Checkbox';
import CssBaseline from '@mui/material/CssBaseline';
import Divider from '@mui/material/Divider';
import FormControlLabel from '@mui/material/FormControlLabel';
import FormLabel from '@mui/material/FormLabel';
import FormControl from '@mui/material/FormControl';
import Link from '@mui/material/Link';
import TextField from '@mui/material/TextField';
import Typography from '@mui/material/Typography';
import Stack from '@mui/material/Stack';
import MuiCard from '@mui/material/Card';
import { styled } from '@mui/material/styles';
import AppTheme from '../../components/shared-theme/AppTheme';
import Grid from '@mui/material/Grid';
import Tooltip from '@mui/material/Tooltip';

// Styled TextField component for consistent error styling
const StyledTextField = styled(TextField)(({ theme, error }) => ({
  '& .MuiOutlinedInput-root': {
    '& fieldset': {
      borderColor: error ? '#d32f2f' : 'inherit',
      borderWidth: error ? '2px' : '1px',
    },
    '&:hover fieldset': {
      borderColor: error ? '#d32f2f' : 'inherit',
    },
    '&.Mui-focused fieldset': {
      borderColor: error ? '#d32f2f' : theme.palette.primary.main,
    },
  },
  '& .MuiFormHelperText-root': {
    minHeight: '1.25rem',
    margin: theme.spacing(0.5, 0, 0),
  },
}));

const Card = styled(MuiCard)(({ theme }) => ({
  display: 'flex',
  flexDirection: 'column',
  alignSelf: 'center',
  width: '100%',
  padding: theme.spacing(4),
  gap: theme.spacing(2),
  margin: 'auto',
  boxShadow:
    'hsla(220, 30%, 5%, 0.05) 0px 5px 15px 0px, hsla(220, 25%, 10%, 0.05) 0px 15px 35px -5px',
  [theme.breakpoints.up('sm')]: {
    width: '450px',
  },
  ...theme.applyStyles('dark', {
    boxShadow:
      'hsla(220, 30%, 5%, 0.5) 0px 5px 15px 0px, hsla(220, 25%, 10%, 0.08) 0px 15px 35px -5px',
  }),
}));

const SignUpContainer = styled(Stack)(({ theme }) => ({
  height: "105vh",
  minHeight: "100%",
  padding: theme.spacing(2),
  position: "relative",
  display: "flex",
  alignItems: "center",
  justifyContent: "center",
  [theme.breakpoints.up("sm")]: {
    padding: theme.spacing(4),
  },

  "&::before": {
    content: '""',
    position: "absolute",
    top: 0,
    left: 0,
    width: "100%",
    height: "100%",
    backgroundImage: "url('/background.jpg')",
    backgroundSize: "cover",
    backgroundPosition: "center",
    backgroundRepeat: "no-repeat",
    filter: "blur(10px)",
    zIndex: -1,
  },
}));

export default function SignUp(props) {
  const [formData, setFormData] = React.useState({
    firstName: '',
    lastName: '',
    email: '',
    phone: '',
    cin: '',
    address: '',
    speciality: '',
    dateOfBirth: '',
    password: ''
  });

  const [errors, setErrors] = React.useState({
    firstName: '',
    lastName: '',
    email: '',
    password: '',
    phone: '',
    cin: '',
    address: '',
    speciality: '',
    dateOfBirth: ''
  });

  const ErrorTextField = ({ error, helperText, ...props }) => {
    const [isFocused, setIsFocused] = React.useState(false);

    return (
      <Tooltip 
        open={!!error && !isFocused}
        title={helperText || ''}
        placement="top"
        arrow
      >
        <div style={{ width: '100%' }}>
          <StyledTextField
            {...props}
            error={!!error}
            helperText={helperText}
            onFocus={(e) => {
              setIsFocused(true);
              if (props.onFocus) props.onFocus(e);
            }}
            onBlur={(e) => {
              setIsFocused(false);
              if (props.onBlur) props.onBlur(e);
            }}
          />
        </div>
      </Tooltip>
    );
  };
  const handleChange = (event) => {
    const { name, value, type, checked } = event.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));

    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const validateInputs = () => {
    const newErrors = {};

    if (!formData.email || !/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'Veuillez entrer une adresse email valide.';
    }

    if (!formData.password || formData.password.length < 6) {
      newErrors.password = 'Le mot de passe doit contenir au moins 6 caractères.';
    }

    if (!formData.firstName) {
      newErrors.firstName = 'Le prénom est requis.';
    }

    if (!formData.lastName) {
      newErrors.lastName = 'Le nom est requis.';
    }

    if (!formData.phone || !/^\+?\d{8,}$/.test(formData.phone)) {
      newErrors.phone = 'Numéro de téléphone invalide.';
    }

    if (!formData.cin || !/^\d{8}$/.test(formData.cin)) {
      newErrors.cin = 'CIN invalide (8 chiffres requis).';
    }

    if (!formData.address) {
      newErrors.address = 'L\'adresse est requise.';
    }

    if (!formData.speciality) {
      newErrors.speciality = 'La spécialité est requise.';
    }

    if (!formData.dateOfBirth) {
      newErrors.dateOfBirth = 'La date de naissance est requise.';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (!validateInputs()) {
      return;
    }

    const payload = {
      ...formData,
      role: 'doctor'
    };
    try {
      const response = await fetch(`${process.env.REACT_APP_API_URL}/auth/register`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(payload),
      });

      if (!response.ok) {
        const result = await response.json();
        console.log("Réponse du serveur:", result);
        if (result.message === 'User already exists') {
          setErrors(prev => ({
            ...prev,
            userExists: 'Cet utilisateur existe déjà.'
          }));
        }
        throw new Error('Une erreur est survenue lors de l\'inscription.');
      }
      const result = await response.json();
      console.log("Réponse du serveur:", result);
    } 
    catch (error) {
      console.error(error);
    }
  };

  return (
    <AppTheme {...props}>
      <CssBaseline enableColorScheme />
      <SignUpContainer direction="column">
        {errors.userExists && (
          <Typography 
            color="error" 
            sx={{ 
              position: 'absolute', 
              top: '110px',
              left: '50%', 
              transform: 'translateX(-50%)', 
              textAlign: 'center', 
              width: '100%', 
              zIndex: 10 
            }}
          >
            {errors.userExists}
          </Typography>
        )}
        <Card variant="outlined">
          <Typography
            component="h1"
            variant="h4"
            sx={{ width: '100%', fontSize: 'clamp(2rem, 10vw, 2.15rem)' }}
          >
            S'inscrire
          </Typography>
          <Box component="form" sx={{ display: 'flex', flexDirection: 'column', gap: 2 }} onSubmit={handleSubmit}>
            <Grid container spacing={2}>
              <Grid item xs={12} sm={6}>
                <FormControl fullWidth>
                  <FormLabel>Prénom</FormLabel>
                  <ErrorTextField
                    required
                    fullWidth
                    name="firstName"
                    value={formData.firstName}
                    onChange={handleChange}
                    error={!!errors.firstName}
                    helperText={errors.firstName}
                    placeholder="John"
                  />
                </FormControl>
              </Grid>

              <Grid item xs={12} sm={6}>
                <FormControl fullWidth>
                  <FormLabel>Nom</FormLabel>
                  <ErrorTextField
                    required
                    fullWidth
                    name="lastName"
                    value={formData.lastName}
                    onChange={handleChange}
                    error={!!errors.lastName}
                    helperText={errors.lastName}
                    placeholder="Doe"
                  />
                </FormControl>
              </Grid>

              <Grid item xs={12} sm={6}>
                <FormControl fullWidth>
                  <FormLabel>Email</FormLabel>
                  <ErrorTextField
                    required
                    fullWidth
                    name="email"
                    type="email"
                    value={formData.email}
                    onChange={handleChange}
                    error={!!errors.email}
                    helperText={errors.email}
                    placeholder="exemple@email.com"
                  />
                </FormControl>
              </Grid>

              <Grid item xs={12} sm={6}>
                <FormControl fullWidth>
                  <FormLabel>Téléphone</FormLabel>
                  <ErrorTextField
                    required
                    fullWidth
                    name="phone"
                    value={formData.phone}
                    onChange={handleChange}
                    error={!!errors.phone}
                    helperText={errors.phone}
                    placeholder="+216 99 999 999"
                  />
                </FormControl>
              </Grid>

              <Grid item xs={12} sm={6}>
                <FormControl fullWidth>
                  <FormLabel>CIN</FormLabel>
                  <ErrorTextField
                    required
                    fullWidth
                    name="cin"
                    value={formData.cin}
                    onChange={handleChange}
                    error={!!errors.cin}
                    helperText={errors.cin}
                    placeholder="12345678"
                  />
                </FormControl>
              </Grid>

              <Grid item xs={12} sm={6}>
                <FormControl fullWidth>
                  <FormLabel>Adresse</FormLabel>
                  <ErrorTextField
                    required
                    fullWidth
                    name="address"
                    value={formData.address}
                    onChange={handleChange}
                    error={!!errors.address}
                    helperText={errors.address}
                    placeholder="Rue, Ville, Pays"
                  />
                </FormControl>
              </Grid>

              <Grid item xs={12} sm={6}>
                <FormControl fullWidth>
                  <FormLabel>Spécialité</FormLabel>
                  <ErrorTextField
                    required
                    fullWidth
                    name="speciality"
                    value={formData.speciality}
                    onChange={handleChange}
                    error={!!errors.speciality}
                    helperText={errors.speciality}
                    placeholder="Dentiste"
                  />
                </FormControl>
              </Grid>

              <Grid item xs={12} sm={6}>
                <FormControl fullWidth>
                  <FormLabel>Date de naissance</FormLabel>
                  <ErrorTextField
                    required
                    fullWidth
                    name="dateOfBirth"
                    type="date"
                    value={formData.dateOfBirth}
                    onChange={handleChange}
                    error={!!errors.dateOfBirth}
                    helperText={errors.dateOfBirth}
                  />
                </FormControl>
              </Grid>

              <Grid item xs={12} sm={6}>
                <FormControl fullWidth>
                  <FormLabel>Mot de passe</FormLabel>
                  <ErrorTextField
                    required
                    fullWidth
                    name="password"
                    type="password"
                    value={formData.password}
                    onChange={handleChange}
                    error={!!errors.password}
                    helperText={errors.password}
                    placeholder="••••••"
                  />
                </FormControl>
              </Grid>
            </Grid>

            <Button type="submit" fullWidth variant="contained">
              S'inscrire
            </Button>
          </Box>

          <Typography sx={{ textAlign: 'center' }}>
            <Divider>ou</Divider>
            Déjà un compte ? <Link href="/signin" variant="body2">Se connecter</Link>
          </Typography>
        </Card>
      </SignUpContainer>
    </AppTheme>
  );
}
