import * as React from 'react';
import {
  Box,
  Button,
  CssBaseline,
  FormControl,
  FormLabel,
  Grid,
  Link,
  TextField,
  Typography,
  Stack,
  Card as MuiCard,
  Tooltip
} from '@mui/material';
import { styled } from '@mui/material/styles';
import AppTheme from '../../components/shared-theme/AppTheme';

// Styled Card Component
const Card = styled(MuiCard)(({ theme }) => ({
  display: 'flex',
  flexDirection: 'column',
  alignSelf: 'center',
  width: '100%',
  maxHeight: '90vh', // Prevent content from disappearing
  overflow: 'auto', // Enables scrolling if needed
  padding: theme.spacing(4),
  gap: theme.spacing(2),
  margin: 'auto',
  boxShadow: 'hsla(220, 30%, 5%, 0.05) 0px 5px 15px 0px, hsla(220, 25%, 10%, 0.05) 0px 15px 35px -5px',
  [theme.breakpoints.up('sm')]: {
    width: '450px',
  },
}));

// Styled Container
const SignUpContainer = styled(Stack)(({ theme }) => ({
  height: "100vh",
  minHeight: "100%",
  padding: theme.spacing(2),
  position: "relative",
  display: "flex",
  alignItems: "center",
  justifyContent: "center",
  overflow: "auto",
  backgroundImage: `url('/assets/bg-image.jpg')`, // Change the path as needed
  backgroundSize: "cover",
  backgroundPosition: "center",
  backgroundRepeat: "no-repeat",
  [theme.breakpoints.up("sm")]: {
    padding: theme.spacing(4),
  },
}));


export default function SignUp(props) {
  const [formData, setFormData] = React.useState({
    firstName: '',
    lastName: '',
    email: '',
    phone: '',
    cin: '',
    address: '',
    speciality: '',
    dateOfBirth: '',
    password: ''
  });

  const [errors, setErrors] = React.useState({});

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const validateInputs = () => {
    const newErrors = {};

    if (!formData.email || !/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'Veuillez entrer une adresse email valide.';
    }

    if (!formData.password || formData.password.length < 6) {
      newErrors.password = 'Le mot de passe doit contenir au moins 6 caractères.';
    }

    if (!formData.firstName) {
      newErrors.firstName = 'Le prénom est requis.';
    }

    if (!formData.lastName) {
      newErrors.lastName = 'Le nom est requis.';
    }

    if (!formData.phone || !/^\+?\d{8,}$/.test(formData.phone)) {
      newErrors.phone = 'Numéro de téléphone invalide.';
    }

    if (!formData.cin || !/^\d{8}$/.test(formData.cin)) {
      newErrors.cin = 'CIN invalide (8 chiffres requis).';
    }

    if (!formData.address) {
      newErrors.address = "L'adresse est requise.";
    }

    if (!formData.speciality) {
      newErrors.speciality = 'La spécialité est requise.';
    }

    if (!formData.dateOfBirth) { 
      newErrors.dateOfBirth = 'La date de naissance est requise.';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (!validateInputs()) {
      return;
    }

    try {
      const response = await fetch(`${process.env.REACT_APP_API_URL}/auth/register`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ ...formData, role: 'doctor' }),
      });

      const result = await response.json();
      if (!response.ok) {
        if (result.message === 'User already exists') {
          setErrors(prev => ({ ...prev, userExists: 'Cet utilisateur existe déjà.' }));
        }
        throw new Error('Une erreur est survenue lors de l\'inscription.');
      }

      console.log("Réponse du serveur:", result);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <AppTheme {...props}>
      <CssBaseline enableColorScheme />
      <SignUpContainer direction="column">
        {errors.userExists && (
          <Typography color="error" sx={{ position: 'absolute', top: '110px', left: '50%', transform: 'translateX(-50%)', textAlign: 'center', width: '100%', zIndex: 10 }}>
            {errors.userExists}
          </Typography>
        )}
        <Card variant="outlined">
          <Typography component="h1" variant="h4" sx={{ width: '100%', fontSize: 'clamp(2rem, 10vw, 2.15rem)' }}>
            S'inscrire
          </Typography>

          <Box component="form" sx={{ display: 'flex', flexDirection: 'column', gap: 2 }} onSubmit={handleSubmit}>
            <Grid container spacing={2}>
              {[
                { name: "firstName", label: "Prénom", placeholder: "John" },
                { name: "lastName", label: "Nom", placeholder: "Doe" },
                { name: "email", label: "Email", placeholder: "exemple@email.com", type: "email" },
                { name: "phone", label: "Téléphone", placeholder: "+216 99 999 999" },
                { name: "cin", label: "CIN", placeholder: "12345678" },
                { name: "address", label: "Adresse", placeholder: "Rue, Ville, Pays" },
                { name: "speciality", label: "Spécialité", placeholder: "Dentiste" },
                { name: "dateOfBirth", label: "Date de naissance", type: "date" },
                { name: "password", label: "Mot de passe", type: "password", placeholder: "••••••" }
              ].map(({ name, label, type = "text", placeholder }) => (
                <Grid item xs={12} sm={6} key={name}>
                  <FormControl fullWidth>
                    <FormLabel>{label}</FormLabel>
                    <Tooltip title={errors[name] || ''} open={!!errors[name]} arrow>
                      <TextField
                        required
                        fullWidth
                        name={name}
                        type={type}
                        value={formData[name]}
                        onChange={handleChange}
                        error={!!errors[name]}
                        placeholder={placeholder}
                      />
                    </Tooltip>
                  </FormControl>
                </Grid>
              ))}
            </Grid>

            <Button type="submit" fullWidth variant="contained">
              S'inscrire
            </Button>
          </Box>

          <Typography sx={{ textAlign: 'center' }}>
            Déjà un compte ? <Link href="/signin" variant="body2">Se connecter</Link>
          </Typography>
        </Card>
      </SignUpContainer>
    </AppTheme>
  );
}
