import * as React from 'react';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import CssBaseline from '@mui/material/CssBaseline';
import Divider from '@mui/material/Divider';
import FormControl from '@mui/material/FormControl';
import FormLabel from '@mui/material/FormLabel';
import Link from '@mui/material/Link';
import TextField from '@mui/material/TextField';
import Typography from '@mui/material/Typography';
import Stack from '@mui/material/Stack';
import MuiCard from '@mui/material/Card';
import { styled } from '@mui/material/styles';
import AppTheme from '../../components/shared-theme/AppTheme';
import Grid from '@mui/material/Grid';

const Card = styled(MuiCard)(({ theme }) => ({
  display: 'flex',
  flexDirection: 'column',
  alignSelf: 'center',
  width: '100%',
  padding: theme.spacing(4),
  gap: theme.spacing(2),
  margin: 'auto',
  boxShadow:
    'hsla(220, 30%, 5%, 0.05) 0px 5px 15px 0px, hsla(220, 25%, 10%, 0.05) 0px 15px 35px -5px',
  maxHeight: '90vh', // Prevents content from being pushed out
  overflow: 'auto', // Enables scrolling
  [theme.breakpoints.up('sm')]: {
    width: '450px',
  },
}));

const SignUpContainer = styled(Stack)(({ theme }) => ({
  height: "100vh",
  minHeight: "100%",
  padding: theme.spacing(2),
  position: "relative",
  display: "flex",
  alignItems: "center",
  justifyContent: "center",
  [theme.breakpoints.up("sm")]: {
    padding: theme.spacing(4),
  },
  "&::before": {
    content: '""',
    position: "absolute",
    top: 0,
    left: 0,
    width: "100%",
    height: "100%",
    backgroundImage: "url('/background.jpg')",
    backgroundSize: "cover",
    backgroundPosition: "center",
    backgroundRepeat: "no-repeat",
    filter: "blur(10px)",
    zIndex: -1,
  },
}));

export default function SignUp(props) {
  const [formData, setFormData] = React.useState({
    firstName: '',
    lastName: '',
    email: '',
    phone: '',
    cin: '',
    address: '',
    speciality: '',
    dateOfBirth: '',
    password: ''
  });

  const [errors, setErrors] = React.useState({});

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData(prev => ({ ...prev, [name]: value }));

    if (errors[name]) {
      setErrors(prev => ({ ...prev, [name]: '' }));
    }
  };

  const validateInputs = () => {
    const newErrors = {};

    if (!formData.email || !/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'Veuillez entrer une adresse email valide.';
    }

    if (!formData.password || formData.password.length < 6) {
      newErrors.password = 'Le mot de passe doit contenir au moins 6 caractères.';
    }

    if (!formData.firstName) newErrors.firstName = 'Le prénom est requis.';
    if (!formData.lastName) newErrors.lastName = 'Le nom est requis.';
    if (!formData.address) newErrors.address = 'L\'adresse est requise.';
    if (!formData.speciality) newErrors.speciality = 'La spécialité est requise.';
    if (!formData.dateOfBirth) newErrors.dateOfBirth = 'La date de naissance est requise.';

    if (!formData.phone || !/^\+?\d{8,}$/.test(formData.phone)) {
      newErrors.phone = 'Numéro de téléphone invalide.';
    }

    if (!formData.cin || !/^\d{8}$/.test(formData.cin)) {
      newErrors.cin = 'CIN invalide (8 chiffres requis).';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (!validateInputs()) return;

    const payload = { ...formData, role: 'doctor' };

    try {
      const response = await fetch(`${process.env.REACT_APP_API_URL}/auth/register`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload),
      });

      if (!response.ok) {
        const result = await response.json();
        if (result.message === 'User already exists') {
          setErrors(prev => ({ ...prev, userExists: 'Cet utilisateur existe déjà.' }));
        }
        throw new Error('Une erreur est survenue lors de l\'inscription.');
      }

      console.log("Réponse du serveur:", await response.json());
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <AppTheme {...props}>
      <CssBaseline enableColorScheme />
      <SignUpContainer direction="column">
        {errors.userExists && (
          <Typography color="error" sx={{ textAlign: 'center', mb: 2 }}>
            {errors.userExists}
          </Typography>
        )}

        <Card variant="outlined">
          <Typography component="h1" variant="h4" sx={{ fontSize: 'clamp(2rem, 10vw, 2.15rem)' }}>
            S'inscrire
          </Typography>

          <Box component="form" sx={{ display: 'flex', flexDirection: 'column', gap: 2 }} onSubmit={handleSubmit}>
            <Grid container spacing={2}>
              {[
                { name: 'firstName', label: 'Prénom', placeholder: 'John' },
                { name: 'lastName', label: 'Nom', placeholder: 'Doe' },
                { name: 'email', label: 'Email', type: 'email', placeholder: 'exemple@email.com' },
                { name: 'phone', label: 'Téléphone', placeholder: '+216 99 999 999' },
                { name: 'cin', label: 'CIN', placeholder: '12345678' },
                { name: 'address', label: 'Adresse', placeholder: 'Rue, Ville, Pays' },
                { name: 'speciality', label: 'Spécialité', placeholder: 'Dentiste' },
                { name: 'dateOfBirth', label: 'Date de naissance', type: 'date' },
                { name: 'password', label: 'Mot de passe', type: 'password', placeholder: '••••••' }
              ].map(({ name, label, type = 'text', placeholder }) => (
                <Grid item xs={12} sm={6} key={name}>
                  <FormControl fullWidth>
                    <FormLabel>{label}</FormLabel>
                    <TextField
                      required
                      fullWidth
                      name={name}
                      type={type}
                      value={formData[name]}
                      onChange={handleChange}
                      error={!!errors[name]}
                      helperText={errors[name] || ' '}
                      placeholder={placeholder}
                      FormHelperTextProps={{ sx: { minHeight: '20px' } }}
                    />
                  </FormControl>
                </Grid>
              ))}
            </Grid>

            <Button type="submit" fullWidth variant="contained">
              S'inscrire
            </Button>
          </Box>

          <Typography sx={{ textAlign: 'center' }}>
            <Divider>ou</Divider>
            Déjà un compte ? <Link href="/signin" variant="body2">Se connecter</Link>
          </Typography>
        </Card>
      </SignUpContainer>
    </AppTheme>
  );
}
